import{_ as r,c as e,o as a,a1 as t}from"./chunks/framework.CVttuHQ1.js";const p=JSON.parse('{"title":"Enkla Projektidéer","description":"","frontmatter":{"outline":[1,6]},"headers":[],"relativePath":"school-work/part3/index.md","filePath":"school-work/part3/index.md"}'),n={name:"school-work/part3/index.md"},o=t('<h1 id="enkla-projektideer" tabindex="-1">Enkla Projektidéer <a class="header-anchor" href="#enkla-projektideer" aria-label="Permalink to &quot;Enkla Projektidéer&quot;">​</a></h1><p>Här är en lista med 15 enkla projektidéer med Arduino som passar bra för dig (gymnasieelev) och nybörjare inom programmering och elektronik. Dessa projekt kombinerar grundläggande teknik, sensorer och motorstyrning, och kan enkelt anpassas beroende på din kunskapsnivå.</p><h2 id="_1-temperaturmatare-med-lcd-display" tabindex="-1">1. Temperaturmätare med LCD-display <a class="header-anchor" href="#_1-temperaturmatare-med-lcd-display" aria-label="Permalink to &quot;1. Temperaturmätare med LCD-display&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en temperaturmätare med en temperaturgivare som visar värdet på en LCD-skärm.</li><li><strong>Lärdom</strong>: Grunder i att läsa analoga värden från sensorer och visa dem på en display.</li><li><strong>Komponenter</strong>: LM35 eller DHT11 temperaturgivare, 16x2 LCD-display, potentiometer.</li></ul><h2 id="_2-ultraljudsbaserad-avstandsmatare" tabindex="-1">2. Ultraljudsbaserad avståndsmätare <a class="header-anchor" href="#_2-ultraljudsbaserad-avstandsmatare" aria-label="Permalink to &quot;2. Ultraljudsbaserad avståndsmätare&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Använd en ultraljudssensor för att mäta avstånd och visa det på en skärm eller använda lysdioder för att signalera olika avstånd.</li><li><strong>Lärdom</strong>: Användning av sensorer, avståndsmätning och signalbehandling.</li><li><strong>Komponenter</strong>: HC-SR04 ultraljudssensor, LED-lampor eller display.</li></ul><h2 id="_3-ljusdetektor-ldr" tabindex="-1">3. Ljusdetektor (LDR) <a class="header-anchor" href="#_3-ljusdetektor-ldr" aria-label="Permalink to &quot;3. Ljusdetektor (LDR)&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en ljussensor som kan reagera på förändringar i ljusstyrka genom att tända eller släcka en lampa.</li><li><strong>Lärdom</strong>: Användning av ljussensorn (LDR), analog inläsning och grundläggande logik.</li><li><strong>Komponenter</strong>: LDR (Light Dependent Resistor), LED, motstånd.</li></ul><h2 id="_4-fuktighetsmatare-for-jord-vaxtvattnare" tabindex="-1">4. Fuktighetsmätare för jord (växtvattnare) <a class="header-anchor" href="#_4-fuktighetsmatare-for-jord-vaxtvattnare" aria-label="Permalink to &quot;4. Fuktighetsmätare för jord (växtvattnare)&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en enkel fuktighetsmätare för att övervaka fuktighetsnivåerna i jorden och aktivera ett larm eller vattningssystem när jorden blir för torr.</li><li><strong>Lärdom</strong>: Grunder i analog inläsning och aktivering av utgångar baserat på sensorvärden.</li><li><strong>Komponenter</strong>: Jordfuktighetssensor, buzzer, LED.</li></ul><h2 id="_5-digital-termometer-med-larm" tabindex="-1">5. Digital termometer med larm <a class="header-anchor" href="#_5-digital-termometer-med-larm" aria-label="Permalink to &quot;5. Digital termometer med larm&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Mät temperaturen och trigga ett ljudlarm om den når en viss nivå, till exempel för att övervaka omgivningstemperaturen.</li><li><strong>Lärdom</strong>: Temperaturmätning och logik för att styra ett larm.</li><li><strong>Komponenter</strong>: DHT11 temperaturgivare, buzzer, LED.</li></ul><h2 id="_6-rorelsedetektor-med-pir-sensor" tabindex="-1">6. Rörelsedetektor med PIR-sensor <a class="header-anchor" href="#_6-rorelsedetektor-med-pir-sensor" aria-label="Permalink to &quot;6. Rörelsedetektor med PIR-sensor&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en rörelsedetektor som aktiverar ett larm eller tänder en LED när rörelse upptäcks i ett område.</li><li><strong>Lärdom</strong>: Användning av PIR-sensorer (Passive Infrared Sensor) och händelsebaserad programmering.</li><li><strong>Komponenter</strong>: PIR-sensor, LED, buzzer.</li></ul><h2 id="_7-fjarrstyrd-bil-med-bluetooth" tabindex="-1">7. Fjärrstyrd bil med Bluetooth <a class="header-anchor" href="#_7-fjarrstyrd-bil-med-bluetooth" aria-label="Permalink to &quot;7. Fjärrstyrd bil med Bluetooth&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en liten bil som kan styras via en smartphone med Bluetooth-kommunikation.</li><li><strong>Lärdom</strong>: Trådlös kommunikation och motorstyrning.</li><li><strong>Komponenter</strong>: DC-motorer, motorstyrkort, Bluetooth-modul, batteri.</li></ul><h2 id="_8-hjartslagmatare-med-pulsensor" tabindex="-1">8. Hjärtslagmätare med pulsensor <a class="header-anchor" href="#_8-hjartslagmatare-med-pulsensor" aria-label="Permalink to &quot;8. Hjärtslagmätare med pulsensor&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Använd en pulsensor för att mäta hjärtslag och visa värdet på en skärm eller LED-indikator.</li><li><strong>Lärdom</strong>: Användning av sensorer för att mäta biologiska signaler.</li><li><strong>Komponenter</strong>: Pulsensor, 16x2 LCD-skärm eller LED-lampor.</li></ul><h2 id="_9-smarta-persienner" tabindex="-1">9. Smarta persienner <a class="header-anchor" href="#_9-smarta-persienner" aria-label="Permalink to &quot;9. Smarta persienner&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Programmera ett system som automatiskt öppnar och stänger persienner beroende på ljusförhållanden eller tid på dagen.</li><li><strong>Lärdom</strong>: Automatisering och användning av tidsscheman eller sensorer.</li><li><strong>Komponenter</strong>: LDR, servomotorer, relämodul.</li></ul><h2 id="_10-fartmatare-for-roterande-objekt" tabindex="-1">10. Fartmätare för roterande objekt <a class="header-anchor" href="#_10-fartmatare-for-roterande-objekt" aria-label="Permalink to &quot;10. Fartmätare för roterande objekt&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en enkel fartmätare som kan mäta hastigheten på ett roterande objekt, som en fläkt, med hjälp av en IR-sensor.</li><li><strong>Lärdom</strong>: Användning av infraröd detektion och beräkning av rotationshastigheter.</li><li><strong>Komponenter</strong>: IR-sensor, display eller LED-indikator.</li></ul><h2 id="_11-automatisk-dorroppnare" tabindex="-1">11. Automatisk dörröppnare <a class="header-anchor" href="#_11-automatisk-dorroppnare" aria-label="Permalink to &quot;11. Automatisk dörröppnare&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Skapa ett system som automatiskt öppnar en dörr när en rörelse upptäcks.</li><li><strong>Lärdom</strong>: Motorstyrning, sensoranvändning och mekanik.</li><li><strong>Komponenter</strong>: Servomotor eller DC-motor, PIR-sensor, strömförsörjning.</li></ul><h2 id="_12-ljudaktiverad-lampa-klappa-for-att-tanda-slacka" tabindex="-1">12. Ljudaktiverad lampa (klappa för att tända/släcka) <a class="header-anchor" href="#_12-ljudaktiverad-lampa-klappa-for-att-tanda-slacka" aria-label="Permalink to &quot;12. Ljudaktiverad lampa (klappa för att tända/släcka)&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en lampa som reagerar på ljud (som klappande händer) för att tändas eller släckas.</li><li><strong>Lärdom</strong>: Använda en mikrofonmodul för att detektera ljud och styra en utgång.</li><li><strong>Komponenter</strong>: Mikrofonmodul, LED-lampa, relämodul.</li></ul><h2 id="_13-arduino-baserad-dorrlarm" tabindex="-1">13. Arduino-baserad dörrlarm <a class="header-anchor" href="#_13-arduino-baserad-dorrlarm" aria-label="Permalink to &quot;13. Arduino-baserad dörrlarm&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Skapa ett larm som aktiveras när en dörr öppnas med hjälp av en magnetisk reed-switch.</li><li><strong>Lärdom</strong>: Användning av enkla sensorer och larmfunktionalitet.</li><li><strong>Komponenter</strong>: Reed-switch, magnet, buzzer.</li></ul><h2 id="_14-smarta-gatlampor-med-rorelsesensor" tabindex="-1">14. Smarta gatlampor med rörelsesensor <a class="header-anchor" href="#_14-smarta-gatlampor-med-rorelsesensor" aria-label="Permalink to &quot;14. Smarta gatlampor med rörelsesensor&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Skapa ett system där gatlampor tänds när en bil eller person närmar sig och släcks när ingen rörelse upptäcks.</li><li><strong>Lärdom</strong>: Effektiv energihantering och användning av PIR-sensorer.</li><li><strong>Komponenter</strong>: PIR-sensor, LED-lampor, relämodul.</li></ul><h2 id="_15-enkel-vaderstation" tabindex="-1">15. Enkel väderstation <a class="header-anchor" href="#_15-enkel-vaderstation" aria-label="Permalink to &quot;15. Enkel väderstation&quot;">​</a></h2><ul><li><strong>Beskrivning</strong>: Bygg en enkel väderstation som mäter och visar temperatur, luftfuktighet och lufttryck.</li><li><strong>Lärdom</strong>: Sensorintegration och datainsamling från flera källor.</li><li><strong>Komponenter</strong>: Arduino Uno, DHT22 eller DHT11 sensor (Meter temperatur och luftfuktighet), BMP180 eller BMP280 sensor (Mäter lufttryck), LCD-skärm (16x2) eller OLED-skärm.</li></ul>',32),s=[o];function l(i,d,m,g,u,k){return a(),e("div",null,s)}const c=r(n,[["render",l]]);export{p as __pageData,c as default};
